define('core_course/learning_path', ['jquery'], function($)
{

        return{
                init: function()
                {
                   $(document).ready(function() {
            		$("#submit").click(function() {

		var length = $('.custom-select').length;

		var i;
               	var select_array = [];
                for(i=1; i<=length; i++)
                {
			select_array.push($("#tag"+i).val());
                }                        
               	var select_data = JSON.stringify(select_array);
