            function ct_pendingApprovalCount(courseidval = 0) {

                if (courseidval == 0) {
                    courseidval = courseid;
                }

                var livemodalstatus = localStorage.getItem(storagePrefixD + "livemodalstatus")
                    // If modal is open, no need to update the kpi data
