                var data = ajax.call([{
                    methodname: 'block_coursetimer_fetch_pendingapproval_count',
                    args: {
                        courseid: courseid
                    }
                }]);

                data[0].done(function(response) {
                    $("#approval_tab").html(" Pending Approvals (" + response.total + ")");
