                            } else if (email.includes(val.toUpperCase())) {
                                /*create a DIV element for each matching element:*/
                                b = document.createElement("DIV");
                                /*make the matching letters bold:*/
                                b.innerHTML = res[2];
                                // b.innerHTML = "<strong>" + res[0].substr(0, val.length) + "</strong>";
