                            $("#internet-back-again").fadeTo(500, 0).slideUp(500, function() {
                                $(this).remove();
                            });
                        }, 5000);
                    }

                    checkResponseData(response);
                    failCount = 0;

                    // Reset notification since the connection is up
                    nointernetNotification = 0;

