                                        setTimeout(function() {
                                            waitingforresponse = false;
                                            notInFullscreen();
                                        }, 3000);
                                    }
                                });

                            } else if (fullScreenCheck != 0 && exceptionUsed < exceptionAllowed) {
                                //show warning message
