				    var element = document.querySelectorAll(selector);        
					var i;
					
					for (i = 0; i < element.length; i++) {
						var truncated = element[i].innerText;
						if (truncated.length > maxLength) {
						    truncated = truncated.substr(0,maxLength) + '...';
